import React from 'react';

class UserProfileForm extends React.Component {
  state = {
    user: {
      firstName: '',
      lastName: '',
      contact: {
        email: '',
        phone: ''
      },
      preferences: {
        newsletter: false,
        smsNotifications: false
      }
    }
  };

  handleTextChange = (event) => {
    const { name, value } = event.target;
    this.setState(prevState => {
      // Split name by dot for nested properties, e.g., 'contact.email'
      const nameParts = name.split('.');
      if (nameParts.length === 1) {
        // Top-level property of user
        return {
          user: {
            ...prevState.user,
            [name]: value
          }
        };
      } else if (nameParts.length === 2) {
        // Nested property, e.g., contact.email or preferences.newsletter
        const [parent, child] = nameParts;
        return {
          user: {
            ...prevState.user,
            [parent]: {
              ...prevState.user[parent],
              [child]: value
            }
          }
        };
      }
      return prevState; // Should not reach here
    });
  };

  handleToggleChange = (event) => {
    const { name, checked } = event.target;
    this.setState(prevState => {
      const nameParts = name.split('.');
      if (nameParts.length === 2) {
        const [parent, child] = nameParts;
        return {
          user: {
            ...prevState.user,
            [parent]: {
              ...prevState.user[parent],
              [child]: checked
            }
          }
        };
      }
      return prevState; // Should not reach here
    });
  };

  render() {
    const { user } = this.state;
    return (
      <div style={{ padding: '20px', border: '1px solid #ccc', borderRadius: '8px' }}>
        <h2>User Profile Editor (Class Component)</h2>
        <div>
          <label>First Name:</label>
          <input type="text" name="firstName" value={user.firstName} onChange={this.handleTextChange} />
        </div>
        <div>
          <label>Last Name:</label>
          <input type="text" name="lastName" value={user.lastName} onChange={this.handleTextChange} />
        </div>
        <h3>Contact Info</h3>
        <div>
          <label>Email:</label>
          <input type="email" name="contact.email" value={user.contact.email} onChange={this.handleTextChange} />
        </div>
        <div>
          <label>Phone:</label>
          <input type="text" name="contact.phone" value={user.contact.phone} onChange={this.handleTextChange} />
        </div>
        <h3>Preferences</h3>
        <div>
          <label>
            <input type="checkbox" name="preferences.newsletter" checked={user.preferences.newsletter} onChange={this.handleToggleChange} />
            Receive Newsletter
          </label>
        </div>
        <div>
          <label>
            <input type="checkbox" name="preferences.smsNotifications" checked={user.preferences.smsNotifications} onChange={this.handleToggleChange} />
            SMS Notifications
          </label>
        </div>
        <hr/>
        <h4>Current State:</h4>
        <pre>{JSON.stringify(user, null, 2)}</pre>
      </div>
    );
  }
}

export default UserProfileForm;

