import React, { useState } from 'react';

function FeatureCounter() {
  const [count, setCount] = useState(0);
  const [inputValue, setInputValue] = useState('');
  const [error, setError] = useState('');

  const increment = () => {
    setCount(prevCount => prevCount + 1);
  };

  const decrement = () => {
    setCount(prevCount => Math.max(0, prevCount - 1));
  };

  const reset = () => {
    setCount(0);
    setError(''); // Clear error on reset
    setInputValue('');
  };

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
    setError(''); // Clear error as user types
  };

  const handleSetCount = () => {
    const num = parseInt(inputValue, 10);
    if (isNaN(num)) {
      setError('Please enter a valid number.');
    } else {
      setCount(num);
      setError('');
    }
  };

  const fastIncrement = () => {
    // Calling functional update multiple times in a batch ensures correct increment
    setCount(prevCount => prevCount + 1);
    setCount(prevCount => prevCount + 1);
    setCount(prevCount => prevCount + 1);
    setCount(prevCount => prevCount + 1);
    setCount(prevCount => prevCount + 1);
  };

  return (
    <div style={{ padding: '20px', border: '1px solid #ccc', borderRadius: '8px' }}>
      <h2>Feature-Rich Counter (Functional Component)</h2>
      <p>Count: {count}</p>
      <button onClick={increment}>Increment</button>
      <button onClick={decrement}>Decrement</button>
      <button onClick={reset}>Reset</button>
      <hr />
      <div>
        <input
          type="text"
          value={inputValue}
          onChange={handleInputChange}
          placeholder="Enter number"
        />
        <button onClick={handleSetCount}>Set Count</button>
        {error && <p style={{ color: 'red' }}>{error}</p>}
      </div>
      <hr />
      <button onClick={fastIncrement}>Fast Increment (+5)</button>
    </div>
  );
};

export default FeatureCounter;

