import React, { useState, useMemo } from 'react';

function ShoppingCart() {
  const [cartItems, setCartItems] = useState([
    { id: 1, name: 'Laptop', price: 1200, quantity: 1 },
    { id: 2, name: 'Mouse', price: 25, quantity: 2 },
    { id: 3, name: 'Keyboard', price: 75, quantity: 1 }
  ]);

  const addItem = (itemToAdd) => {
    setCartItems(prevItems => {
      const existingItem = prevItems.find(item => item.id === itemToAdd.id);
      if (existingItem) {
        // If item exists, update its quantity
        return prevItems.map(item =>
          item.id === itemToAdd.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      } else {
        // If item is new, add it to the cart
        return [...prevItems, { ...itemToAdd, quantity: 1 }];
      }
    });
  };

  const removeItem = (itemId) => {
    setCartItems(prevItems => prevItems.filter(item => item.id !== itemId));
  };

  const updateQuantity = (itemId, newQuantity) => {
    setCartItems(prevItems =>
      prevItems.map(item =>
        item.id === itemId ? { ...item, quantity: Math.max(1, newQuantity) } : item
      )
    );
  };

  // Calculate total price using useMemo for performance
  const total = useMemo(() => {
    return cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);
  }, [cartItems]);

  return (
    <div style={{ padding: '20px', border: '1px solid #ccc', borderRadius: '8px' }}>
      <h2>My Shopping Cart (Functional Component)</h2>
      <div>
        <h3>Items:</h3>
        {cartItems.length === 0 ? (
          <p>Your cart is empty.</p>
        ) : (
          <ul>
            {cartItems.map(item => (
              <li key={item.id} style={{ marginBottom: '10px' }}>
                {item.name} - ${item.price} x 
                <button onClick={() => updateQuantity(item.id, item.quantity - 1)}>-</button>
                {item.quantity}
                <button onClick={() => updateQuantity(item.id, item.quantity + 1)}>+</button>
                (Subtotal: ${(item.price * item.quantity).toFixed(2)})
                <button onClick={() => removeItem(item.id)} style={{ marginLeft: '10px', color: 'red' }}>Remove</button>
              </li>
            ))}
          </ul>
        )}
        <h3>Total: ${total.toFixed(2)}</h3>
      </div>
      <hr />
      <div>
        <h4>Add new items:</h4>
        <button onClick={() => addItem({ id: 4, name: 'Monitor', price: 300 })}>Add Monitor</button>
        <button onClick={() => addItem({ id: 1, name: 'Laptop', price: 1200 })} style={{ marginLeft: '10px' }}>Add Laptop (existing)</button>
      </div>
    </div>
  );
}

export default ShoppingCart;

