```typescript
import React, { useState } from 'react';

function ComplexForm() {
  const [formData, setFormData] = useState({
    personal: {
      firstName: '',
      lastName: '',
      age: 0
    },
    contact: {
      email: '',
      phone: ''
    },
    address: {
      street: '',
      city: '',
      zip: ''
    }
  });

  const handleFormChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;

    setFormData(prevFormData => {
      const keys = name.split('.');
      let newFormData = { ...prevFormData };
      let currentLevel: any = newFormData;

      for (let i = 0; i < keys.length; i++) {
        const key = keys[i];
        if (i === keys.length - 1) {
          // Last key, update the value
          currentLevel[key] = value;
        } else {
          // Not the last key, deep copy the nested object
          currentLevel[key] = { ...currentLevel[key] };
          currentLevel = currentLevel[key];
        }
      }
      return newFormData;
    });
  };

  return (
    <form>
      <h3>Personal Info</h3>
      <input name="personal.firstName" value={formData.personal.firstName} onChange={handleFormChange} placeholder="First Name" />
      <input name="personal.lastName" value={formData.personal.lastName} onChange={handleFormChange} placeholder="Last Name" />
      <input name="personal.age" type="number" value={formData.personal.age} onChange={handleFormChange} placeholder="Age" />

      <h3>Contact Info</h3>
      <input name="contact.email" value={formData.contact.email} onChange={handleFormChange} placeholder="Email" />
      <input name="contact.phone" value={formData.contact.phone} onChange={handleFormChange} placeholder="Phone" />

      <h3>Address</h3>
      <input name="address.street" value={formData.address.street} onChange={handleFormChange} placeholder="Street" />
      <input name="address.city" value={formData.address.city} onChange={handleFormChange} placeholder="City" />
      <input name="address.zip" value={formData.address.zip} onChange={handleFormChange} placeholder="Zip Code" />

      <pre>{JSON.stringify(formData, null, 2)}</pre>
    </form>
  );
}
