```typescript
import React, { useRef, useEffect, useState } from 'react';

// Custom hook to get the previous value of a state or prop
function usePrevious<T>(value: T): T | undefined {
  const ref = useRef<T>();
  useEffect(() => {
    ref.current = value;
  }, [value]);
  return ref.current;
}

interface ScrollableDivProps {
  children: React.ReactNode;
}

function ScrollableDiv({ children }: ScrollableDivProps) {
  const scrollRef = useRef<HTMLDivElement>(null);
  const prevChildren = usePrevious(children);

  useEffect(() => {
    if (scrollRef.current) {
      // Only scroll to bottom if content has changed (e.g., new messages)
      // A more robust check might compare actual message count or scroll height
      // For simplicity, we check if children ref has changed, meaning content likely did
      if (prevChildren !== children) {
         scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
      }
    }
  }, [children, prevChildren]); // Re-run when children content changes

  return (
    <div
      ref={scrollRef}
      style={{
        height: '200px',
        overflowY: 'auto',
        border: '1px solid #ccc',
        padding: '10px',
        display: 'flex',
        flexDirection: 'column'
      }}
    >
      {children}
    </div>
  );
}

// Example Usage:
function ChatWindow() {
  const [messages, setMessages] = useState<string[]>([
    "Hello!",
    "Welcome to the chat."
  ]);

  const addMessage = () => {
    setMessages(prev => [...prev, `New message at ${new Date().toLocaleTimeString()}`]);
  };

  return (
    <div>
      <ScrollableDiv>
        {messages.map((msg, index) => (
          <p key={index}>{msg}</p>
        ))}
      </ScrollableDiv>
      <button onClick={addMessage}>Add Message</button>
    </div>
  );
}
```

