import React, { useState } from 'react';

interface UserProfile {
  firstName: string;
  lastName: string;
  email: string;
  preferences: {
    newsletterSubscription: boolean;
    smsNotifications: boolean;
  };
}

function UserProfileForm() {
  const [user, setUser] = useState<UserProfile>({
    firstName: '',
    lastName: '',
    email: '',
    preferences: {
      newsletterSubscription: false,
      smsNotifications: false,
    },
  });

  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;
    setUser(prevUser => ({
      ...prevUser,
      [name]: value, // Update top-level properties dynamically
    }));
  };

  const handleCheckboxChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, checked } = event.target;
    // For nested preferences, we need nested spread
    setUser(prevUser => ({
      ...prevUser,
      preferences: {
        ...prevUser.preferences,
        [name]: checked,
      },
    }));
  };

  return (
    <div>
      <h2>User Profile Editor</h2>
      <form>
        <label>
          First Name:
          <input
            type="text"
            name="firstName"
            value={user.firstName}
            onChange={handleInputChange}
          />
        </label>
        <br />
        <label>
          Last Name:
          <input
            type="text"
            name="lastName"
            value={user.lastName}
            onChange={handleInputChange}
          />
        </label>
        <br />
        <label>
          Email:
          <input
            type="email"
            name="email"
            value={user.email}
            onChange={handleInputChange}
          />
        </label>
        <br />
        <h3>Preferences</h3>
        <label>
          <input
            type="checkbox"
            name="newsletterSubscription"
            checked={user.preferences.newsletterSubscription}
            onChange={handleCheckboxChange}
          />
          Newsletter Subscription
        </label>
        <br />
        <label>
          <input
            type="checkbox"
            name="smsNotifications"
            checked={user.preferences.smsNotifications}
            onChange={handleCheckboxChange}
          />
          SMS Notifications
        </label>
      </form>
      <h3>Current State:</h3>
      <pre>{JSON.stringify(user, null, 2)}</pre>
    </div>
  );
}

export default UserProfileForm;

