import React, { useState } from 'react';

function Tooltip({ text, children }) {
  const [showTooltip, setShowTooltip] = useState(false);

  const handleMouseEnter = () => {
    setShowTooltip(true);
  };

  const handleMouseLeave = () => {
    setShowTooltip(false);
  };

  return (
    <div
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      style={{ position: 'relative', display: 'inline-block', border: '1px solid #ccc', padding: '5px', margin: '10px' }}
    >
      {children}
      {showTooltip && (
        <div
          style={{
            position: 'absolute',
            top: 'calc(100% + 5px)',
            left: '50%',
            transform: 'translateX(-50%)',
            backgroundColor: '#333',
            color: 'white',
            padding: '8px',
            borderRadius: '4px',
            whiteSpace: 'nowrap',
            zIndex: 1000
          }}
        >
          {text}
        </div>
      )}
    </div>
  );
}

