import React, { useReducer } from 'react';

const initialCartState = {
  items: [],
  totalPrice: 0,
};

function calculateTotalPrice(items) {
  return items.reduce((acc, item) => acc + (item.price * item.quantity), 0);
}

function cartReducer(state, action) {
  let updatedItems;
  switch (action.type) {
    case 'ADD_ITEM':
      const existingItemIndex = state.items.findIndex(item => item.id === action.payload.id);
      if (existingItemIndex > -1) {
        updatedItems = state.items.map((item, index) =>
          index === existingItemIndex
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      } else {
        updatedItems = [...state.items, { ...action.payload, quantity: 1 }];
      }
      return { ...state, items: updatedItems, totalPrice: calculateTotalPrice(updatedItems) };

    case 'REMOVE_ITEM':
      updatedItems = state.items.filter(item => item.id !== action.payload.id);
      return { ...state, items: updatedItems, totalPrice: calculateTotalPrice(updatedItems) };

    case 'UPDATE_QUANTITY':
      updatedItems = state.items.map(item =>
        item.id === action.payload.id
          ? { ...item, quantity: action.payload.quantity }
          : item
      ).filter(item => item.quantity > 0); // Remove if quantity becomes 0 or less
      return { ...state, items: updatedItems, totalPrice: calculateTotalPrice(updatedItems) };

    case 'CLEAR_CART':
      return { ...initialCartState }; // Reset to initial empty state

    default:
      throw new Error(`Unsupported action type: ${action.type}`);
  }
}

function ShoppingCart() {
  const [cartState, dispatch] = useReducer(cartReducer, initialCartState);

  const products = [
    { id: 1, name: 'Laptop', price: 1200 },
    { id: 2, name: 'Mouse', price: 25 },
    { id: 3, name: 'Keyboard', price: 75 }
  ];

  return (
