import React, { useReducer, useEffect, useRef } from 'react';

const initialStopwatchState = {
  isRunning: false,
  time: 0,
  lapTimes: [],
};

function formatTime(milliseconds) {
  const minutes = Math.floor(milliseconds / 60000);
  const seconds = Math.floor((milliseconds % 60000) / 1000);
  const ms = Math.floor((milliseconds % 1000) / 10); // Display centiseconds
  return `${
    minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}.${ms.toString().padStart(2, '0')}`;
}

function stopwatchReducer(state, action) {
  switch (action.type) {
    case 'START':
      return { ...state, isRunning: true };
    case 'STOP':
      return { ...state, isRunning: false };
    case 'RESET':
      return { ...initialStopwatchState }; // Reset to full initial state
    case 'TICK':
      return { ...state, time: state.time + action.payload }; // Payload for interval time
    case 'LAP':
      return { ...state, lapTimes: [...state.lapTimes, state.time] };
    default:
      throw new Error(`Unsupported action type: ${action.type}`);
  }
}

const TICK_INTERVAL = 10; // milliseconds

function Stopwatch() {
  const [state, dispatch] = useReducer(stopwatchReducer, initialStopwatchState);
  const timerIdRef = useRef(null);

  useEffect(() => {
    if (state.isRunning) {
      timerIdRef.current = setInterval(() => {
        dispatch({ type: 'TICK', payload: TICK_INTERVAL });
      }, TICK_INTERVAL);
    } else {
      clearInterval(timerIdRef.current);
      timerIdRef.current = null;
    }

    return () => {
      clearInterval(timerIdRef.current);
      timerIdRef.current = null;
    };
  }, [state.isRunning, dispatch]); // dispatch is stable, so it won't cause re-runs itself

  return (
    <div style={{ textAlign: 'center', fontFamily: 'monospace', padding: '20px', background: '#f8f8f8', borderRadius: '10px', boxShadow: '0 4px 8px rgba(0,0,0,0.1)', maxWidth: '400px', margin: '20px auto' }}>
      <h1 style={{ color: '#333' }}>Stopwatch</h1>
      <p style={{ fontSize: '3.5em', margin: '25px 0', color: '#007bff' }}>{formatTime(state.time)}</p>
      <div style={{ marginBottom: '25px', display: 'flex', justifyContent: 'center', gap: '10px' }}>
        <button 
          onClick={() => dispatch({ type: 'START' })}
          disabled={state.isRunning}
          style={{ padding: '10px 20px', fontSize: '1.1em', backgroundColor: '#28a745', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}
        >
          Start
        </button>
        <button 
          onClick={() => dispatch({ type: 'STOP' })}
          disabled={!state.isRunning}
          style={{ padding: '10px 20px', fontSize: '1.1em', backgroundColor: '#dc3545', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}
        >
          Stop
        </button>
        <button 
          onClick={() => dispatch({ type: 'RESET' })}
          style={{ padding: '10px 20px', fontSize: '1.1em', backgroundColor: '#6c757d', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}
        >
          Reset
        </button>
        <button 
          onClick={() => dispatch({ type: 'LAP' })}
          disabled={!state.isRunning || state.time === 0}
          style={{ padding: '10px 20px', fontSize: '1.1em', backgroundColor: '#ffc107', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}
        >
          Lap
        </button>
      </div>
      <h2 style={{ color: '#555', borderTop: '1px solid #eee', paddingTop: '20px' }}>Lap Times</h2>
      <ul style={{ listStyle: 'none', padding: 0, maxHeight: '150px', overflowY: 'auto', border: '1px solid #eee', borderRadius: '5px', background: 'white', paddingRight: '10px' }}>
        {state.lapTimes.length === 0 ? (
          <li style={{ padding: '8px 0', color: '#888' }}>No lap times recorded.</li>
        ) : (
          state.lapTimes.map((lap, index) => (
            <li key={index} style={{ padding: '8px 0', borderBottom: index < state.lapTimes.length - 1 ? '1px dashed #eee' : 'none', display: 'flex', justifyContent: 'space-between' }}>
              <span>Lap {index + 1}:</span>
              <span style={{ fontWeight: 'bold' }}>{formatTime(lap)}</span>
            </li>
          ))
        )}
      </ul>
    </div>
  );
}

export default Stopwatch;

