import React, { useState, useEffect, useRef } from 'react';

function DraggableBox() {
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const offset = useRef({ x: 0, y: 0 }); // Stores the initial click offset from box's top-left

  const handleMouseDown = (event) => {
    // Prevent default browser drag behavior (e.g., image drag, text selection)
    event.preventDefault(); 
    event.stopPropagation(); // Stop bubbling if nested

    setIsDragging(true);
    // Calculate the offset from the top-left of the box to the mouse click point
    // event.clientX/Y are relative to the viewport
    // event.target.getBoundingClientRect() gives box's position relative to viewport
    const boxRect = event.target.getBoundingClientRect();
    offset.current = {
      x: event.clientX - boxRect.left,
      y: event.clientY - boxRect.top,
    };
  };

  useEffect(() => {
    const handleMouseMove = (event) => {
      if (!isDragging) return;

      // Calculate new position based on mouse move and initial offset
      setPosition({
        x: event.clientX - offset.current.x,
        y: event.clientY - offset.current.y,
      });
    };

    const handleMouseUp = () => {
      setIsDragging(false);
    };

    // Attach listeners to the document to ensure dragging works even if mouse leaves the box
    if (isDragging) {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
    }

    // Cleanup function
    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, [isDragging]); // Re-run effect when isDragging changes

  return (
    <div
      onMouseDown={handleMouseDown}
      style={{
        position: 'absolute',
        left: position.x,
        top: position.y,
        width: '120px',
        height: '120px',
        backgroundColor: isDragging ? '#ffebcd' : 'lightblue',
        border: '2px solid steelblue',
        borderRadius: '8px',
        cursor: isDragging ? 'grabbing' : 'grab',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        userSelect: 'none',
        boxShadow: '0 4px 8px rgba(0,0,0,0.2)'
      }}
    >
      Drag Me
      <br />
      (x:{position.x}, y:{position.y})
    </div>
  );
}

export default DraggableBox;

