import React from 'react';

class Article extends React.Component {
  // Initialize state using class fields syntax
  state = {
    visible: false,
    readCount: 0
  };

  // Event handler to update state
  handleReadMoreClick = (e) => {
    e.preventDefault(); // Prevent default link behavior
    // Using setState to merge the new state with the existing state
    this.setState({
      visible: true,
      readCount: this.state.readCount + 1 // Directly using this.state for simple updates
    });
    console.log('Article visibility updated to:', true);
  };

  render() {
    const { visible, readCount } = this.state;
    return (
      <div>
        <h2>My Awesome Article</h2>
        <p>This is a short introduction...</p>
        {!visible && (
          <a href="#" onClick={this.handleReadMoreClick}>
            Read More
          </a>
        )}
        {visible && (
          <p>This is the full article content that becomes visible after clicking 'Read More'.</p>
        )}
        <p>Times read: {readCount}</p>
      </div>
    );
  }
}

