import React, { useState, useEffect } from 'react';

// Parent component with long content
function LongContentPage() {
  return (
    <div style={{ height: '2000px', background: 'linear-gradient(to bottom, #f0f0f0, #ccc)' }}>
      <h1>Long Page Content</h1>
      {Array.from({ length: 50 }).map((_, i) => (
        <p key={i}>This is paragraph {i + 1}. Scroll down to see the button appear!</p>
      ))}
      <ScrollToTopButton />
    </div>
  );
}

// ScrollToTopButton component
function ScrollToTopButton() {
  const [showButton, setShowButton] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      // Show button if scrolled more than 200px
      if (window.scrollY > 200) {
        setShowButton(true);
      } else {
        setShowButton(false);
      }
    };

    window.addEventListener('scroll', handleScroll);

    // Cleanup the event listener when the component unmounts
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []); // Empty dependency array ensures listener is added/removed once

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth'
    });
  };

  return (
    <div
      style={{
        position: 'fixed',
        bottom: '20px',
        right: '20px',
        padding: '10px 15px',
        backgroundColor: '#007bff',
        color: 'white',
        borderRadius: '5px',
        cursor: 'pointer',
        display: showButton ? 'block' : 'none',
        zIndex: 1000,
        boxShadow: '0 2px 5px rgba(0,0,0,0.2)'
      }}
      onClick={scrollToTop}
    >
      Scroll to Top
    </div>
  );
}

