import React, { useRef, useEffect } from 'react';

// HOC: withLogging that logs renders and forwards refs
function withLogging(WrappedComponent) {
  class WithLogging extends React.Component {
    componentDidMount() {
      console.log(`${WrappedComponent.name || 'Component'} mounted.`);
    }

    render() {
      const { forwardedRef, ...restProps } = this.props;
      return <WrappedComponent ref={forwardedRef} {...restProps} />;
    }
  }

  return React.forwardRef((props, ref) => {
    return <WithLogging {...props} forwardedRef={ref} />;
  });
}

// A simple input component that supports ref forwarding
