  const inputRef = useRef(null);

  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.focus();
      console.log('Input focused via forwarded ref in HOC!');
    }
  }, []);

  return (
    <div>
      <h1>HOC Ref Forwarding Demo</h1>
      <LoggedInput ref={inputRef} placeholder="Enter text here" />
    </div>
  );
}

