// HOC: withHighlightOnMount
function withHighlightOnMount(WrappedComponent) {
  const HighlightWrapper = React.forwardRef((props, ref) => {
    const innerRef = useRef(null);

    // Combine the forwarded ref with our internal ref
    const combinedRef = (node) => {
      innerRef.current = node; // Our internal ref
      if (typeof ref === 'function') {
        ref(node); // Parent's ref (function form)
      } else if (ref) {
        ref.current = node; // Parent's ref (object form)
      }
    };

    useEffect(() => {
      if (innerRef.current) {
        const element = innerRef.current;
        element.classList.add('highlight');

        const timer = setTimeout(() => {
          element.classList.remove('highlight');
        }, 2000);

        return () => clearTimeout(timer);
      }
    }, []);

    return <WrappedComponent ref={combinedRef} {...props} />;
  });
  HighlightWrapper.displayName = `withHighlightOnMount(${WrappedComponent.displayName || WrappedComponent.name || 'Component'})`;
  return HighlightWrapper;
}

// A sample component that accepts a ref
const MyDiv = React.forwardRef((props, ref) => (
  <div ref={ref} style={{ padding: '10px', border: '1px solid gray', margin: '5px' }} className={props.className}>
    {props.children}
  </div>
));

// Create the enhanced component
const HighlightedDiv = withHighlightOnMount(MyDiv);

// Parent component to demonstrate usage
function App() {
  const divRef = useRef(null);

  useEffect(() => {
    // After 3 seconds, log the content of the div via the forwarded ref
    const logTimer = setTimeout(() => {
      if (divRef.current) {
        console.log('Content via forwarded ref after highlight:', divRef.current.innerText);
      }
    }, 3000);
    return () => clearTimeout(logTimer);
  }, []);

  return (
    <div>
      <style>
        {`
          .highlight {
            border: 2px solid red !important;
            background-color: yellow;
            transition: all 0.3s ease-in-out;
          }
        `}
      </style>
      <h1>HOC with Ref Forwarding and Effect</h1>
      <HighlightedDiv ref={divRef}>This div will highlight briefly on mount.</HighlightedDiv>
      <button onClick={() => divRef.current?.innerText = 'Updated via ref!'}>Update Div via Ref</button>
    </div>
  );
}

