import React, { useRef, useEffect } from 'react';

// HOC: withAutoFocus
function withAutoFocus(WrappedComponent) {
  class FocusWrapper extends React.Component {
    componentDidMount() {
      // Check if the forwardedRef exists and has a current property (DOM node)
      if (this.props.forwardedRef && this.props.forwardedRef.current) {
        console.log('AutoFocus HOC: Focusing input!');
        this.props.forwardedRef.current.focus();
      }
    }

    render() {
      const { forwardedRef, ...restProps } = this.props;
      // Pass the forwardedRef to the WrappedComponent as its 'ref' prop
      return <WrappedComponent ref={forwardedRef} {...restProps} />;
    }
  }

  // Use React.forwardRef to pass the parent's ref into the HOC's wrapper component
  const ComponentWithRef = React.forwardRef((props, ref) => {
    // Here, 'ref' is the ref from the component that consumes AutoFocusedInput.
    // We pass it to FocusWrapper as a regular prop named 'forwardedRef'.
    return <FocusWrapper {...props} forwardedRef={ref} />;
  });

  // Good practice for debugging: give the HOC a display name
  ComponentWithRef.displayName = `withAutoFocus(${WrappedComponent.displayName || WrappedComponent.name || 'Component'})`;
  return ComponentWithRef;
}

// A sample WrappedComponent that supports ref forwarding
const MyInput = React.forwardRef((props, ref) => {
  return <input type="text" ref={ref} placeholder={props.placeholder} {...props} />;
});

// Apply the HOC
const AutoFocusedInput = withAutoFocus(MyInput);

function App() {
  const inputRef = useRef(null);

  useEffect(() => {
    // The input should be focused automatically by the HOC
    console.log('App component mounted. Input should be auto-focused.');

    const timer = setTimeout(() => {
      if (inputRef.current) {
        console.log('App: Current input value via forwarded ref:', inputRef.current.value);
      }
    }, 3000);
    return () => clearTimeout(timer);
  }, []);

  const handleButtonClick = () => {
    if (inputRef.current) {
      alert('Current value from parent ref: ' + inputRef.current.value);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Auto-Focused Input Demo (HOC with Ref Forwarding)</h1>
      <p>The input below should be automatically focused on page load.</p>
      <AutoFocusedInput ref={inputRef} placeholder="Type something..." />
      <button onClick={handleButtonClick} style={{ marginLeft: '10px', padding: '8px 15px' }}>Get Value via Parent Ref</button>
      <p style={{ marginTop: '20px', fontSize: '0.9em', color: '#666' }}>Try typing something and then click 'Get Value via Parent Ref'.</p>
