import React from 'react';

// A mock data source for demonstration purposes
const DataSource = {
  data: 'Initial Data',
  listeners: [],
  getData: function(props) {
    // Simulate data selection based on props
    return `Data for ${props.id || 'default'}: ${this.data}`;
  },
  addChangeListener: function(callback) {
    this.listeners.push(callback);
  },
  removeChangeListener: function(callback) {
    this.listeners = this.listeners.filter(cb => cb !== callback);
  },
  // Simulate data changes
  changeData: function(newData) {
    this.data = newData;
    this.listeners.forEach(cb => cb());
  }
};

function withSubscription(WrappedComponent, selectData) {
  return class extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        data: selectData(DataSource, props)
      };
    }

    componentDidMount() {
      // Subscribe to changes
      DataSource.addChangeListener(this.handleChange);
    }

    componentWillUnmount() {
      // Clean up subscription
      DataSource.removeChangeListener(this.handleChange);
    }

    handleChange = () => {
      this.setState({
        data: selectData(DataSource, this.props)
      });
    };

    render() {
      // Pass all props and inject state as additional prop
      // The 'data' prop is injected from the HOC's state.
      return <WrappedComponent data={this.state.data} {...this.props} />;
    }
  };
}

// Usage Example:
function MyDataComponent(props) {
  return (
    <div>
      <h2>Subscribed Data:</h2>
      <p>{props.data}</p>
      <p>Component ID: {props.id}</p>
    </div>
  );
}

