      style={{ borderColor: isValid === false ? 'red' : 'initial', borderWidth: '2px', padding: '5px' }}
    />
  );
});

// 2. Create withValidation HOC that also uses React.forwardRef
function withValidation(WrappedComponent) {
  class WithValidationLogic extends React.Component {
    constructor(props) {
      super(props);
      this.state = { isValid: true };
      this.internalRef = React.createRef(); // Ref to the WrappedComponent instance
    }

    // This method is called by the HOC's consumer (e.g., App) via the forwarded ref.
    // It exposes the WrappedComponent's focus method and the HOC's validate logic.
    exposeMethods = () => ({
      focus: () => {
        if (this.internalRef.current && typeof this.internalRef.current.focus === 'function') {
          this.internalRef.current.focus();
        } else if (this.internalRef.current && this.internalRef.current.nodeType === 1) {
           // If ref is a DOM element (like in CustomInput), focus directly
           this.internalRef.current.focus();
        }
      },
      validate: (value) => {
        const valid = value.trim() !== '';
        this.setState({ isValid: valid });
        return valid;
      },
      // Expose current isValid state
      get isValid() {
        return this.state.isValid;
      }
    });

    render() {
      const { forwardedRef, ...rest } = this.props;

      // Use useImperativeHandle to expose methods through the forwardedRef
      // This must be inside a functional component or use a wrapper class's instance method directly.
      // Here, we adapt for a class component HOC by using a ref callback.
      const setRef = instance => {
        this.internalRef.current = instance;
        if (forwardedRef) {
          if (typeof forwardedRef === 'function') {
            forwardedRef(this.exposeMethods()); // Pass methods to the forwarded ref
          } else {
            forwardedRef.current = this.exposeMethods();
          }
        }
      };

      return (
        <WrappedComponent
          ref={setRef} // Pass our internal ref callback to the WrappedComponent
          isValid={this.state.isValid}
          {...rest}
        />
      );
    }
  }

  // The outer HOC function which creates the ref-aware component
  return React.forwardRef((props, ref) => (
    <WithValidationLogic {...props} forwardedRef={ref} />
  ));
}

// 3. Apply the HOC
const ValidatedInput = withValidation(CustomInput);

// 4. Demonstrate in App component
function App() {
  const inputRef = useRef(null);
  const [inputValue, setInputValue] = useState('');

  const handleFocus = () => {
    if (inputRef.current && typeof inputRef.current.focus === 'function') {
      inputRef.current.focus();
    } else {
      console.warn("Focus method not available on ref.current.");
    }
  };

  const handleValidate = () => {
    if (inputRef.current && typeof inputRef.current.validate === 'function') {
      const isValid = inputRef.current.validate(inputValue);
      alert(`Input is ${isValid ? 'valid' : 'invalid'}`);
    } else {
      console.warn("Validate method not available on ref.current.");
    }
  };

  return (
    <div>
      <h1>Ref Forwarding with HOC Validation</h1>
      <ValidatedInput
        ref={inputRef}
        value={inputValue}
        onChange={(e) => setInputValue(e.target.value)}
        placeholder="Type something..."
      />
      <p>Current input value: {inputValue}</p>
      <p>Input Validity: {inputRef.current?.isValid ? 'Valid' : 'Invalid'}</p>
      <button onClick={handleFocus} style={{ marginRight: '10px' }}>Focus Input</button>
      <button onClick={handleValidate}>Validate Input</button>
    </div>
  );
}

export default App;

