import React from 'react';

function getDisplayName<P extends object>(WrappedComponent: React.ComponentType<P>): string {
  return WrappedComponent.displayName || WrappedComponent.name || 'Component';
}

const simulatedApi = {
  fetchUserData: (userId: string) => new Promise((resolve, reject) => {
    setTimeout(() => {
      if (userId === 'error') {
        reject(new Error('Failed to fetch user data.'));
      } else if (userId === '1') {
        resolve({ id: '1', name: 'Alice', email: 'alice@example.com' });
      } else if (userId === '2') {
        resolve({ id: '2', name: 'Bob', email: 'bob@example.com' });
      } else {
        resolve(null);
      }
    }, 1000);
  }),
  fetchProductData: (productId: string) => new Promise((resolve, reject) => {
    setTimeout(() => {
      if (productId === 'error') {
        reject(new Error('Product data not found.'));
      } else if (productId === 'P1') {
        resolve({ id: 'P1', name: 'Laptop', price: 1200 });
      } else {
        resolve(null);
      }
    }, 800);
  })
};

interface DataLoaderState<D> {
  data: D | null;
  isLoading: boolean;
  error: Error | null;
}

function withDataLoader<P extends object, D>(
  WrappedComponent: React.ComponentType<P & DataLoaderState<D>>,
  dataFetcher: (props: P) => Promise<D | null>
): React.ComponentType<P> {
  class WithDataLoader extends React.Component<P, DataLoaderState<D>> {
    static displayName = `withDataLoader(${getDisplayName(WrappedComponent)})`;

    state: DataLoaderState<D> = {
      data: null,
      isLoading: false,
      error: null,
    };

    private mounted = false; // To prevent setting state on unmounted component

    componentDidMount() {
      this.mounted = true;
      this.fetchData();
    }

    componentDidUpdate(prevProps: P) {
      // Re-fetch data if relevant props change
      if (JSON.stringify(this.props) !== JSON.stringify(prevProps)) {
        this.fetchData();
      }
    }

    componentWillUnmount() {
      this.mounted = false;
    }

    fetchData = async () => {
      if (!this.mounted) return;
      this.setState({ isLoading: true, error: null });
      try {
        const result = await dataFetcher(this.props);
        if (this.mounted) {
          this.setState({ data: result, isLoading: false });
        }
      } catch (err: any) {
        if (this.mounted) {
          this.setState({ error: err, isLoading: false });
        }
      }
    };

    render() {
      const { data, isLoading, error } = this.state;
      return (
        <WrappedComponent
          {...this.props as P}
          data={data}
          isLoading={isLoading}
          error={error}
        />
      );
    }
  }

  return WithDataLoader;
}

interface UserDisplayProps {
  userId: string;
  data: { id: string; name: string; email: string } | null;
  isLoading: boolean;
  error: Error | null;
}

const UserDisplay: React.FC<UserDisplayProps> = ({ userId, data, isLoading, error }) => {
  if (isLoading) return <p>Loading user {userId}...</p>;
  if (error) return <p style={{ color: 'red' }}>Error: {error.message}</p>;
  if (!data) return <p>User {userId} not found.</p>;
  return (
    <div>
      <h3>User Details (ID: {data.id})</h3>
      <p>Name: {data.name}</p>
      <p>Email: {data.email}</p>
    </div>
  );
};

interface ProductDisplayProps {
  productId: string;
  data: { id: string; name: string; price: number } | null;
  isLoading: boolean;
  error: Error | null;
}

const ProductDisplay: React.FC<ProductDisplayProps> = ({ productId, data, isLoading, error }) => {
  if (isLoading) return <p>Loading product {productId}...</p>;
  if (error) return <p style={{ color: 'red' }}>Error: {error.message}</p>;
  if (!data) return <p>Product {productId} not found.</p>;
  return (
    <div>
      <h3>Product Details (ID: {data.id})</h3>
      <p>Name: {data.name}</p>
      <p>Price: ${data.price}</p>
    </div>
  );
};

const UserDataLoader = withDataLoader(UserDisplay, (props: { userId: string }) => simulatedApi.fetchUserData(props.userId));
const ProductDataLoader = withDataLoader(ProductDisplay, (props: { productId: string }) => simulatedApi.fetchProductData(props.productId));

