import React from 'react';

function getDisplayName<P extends object>(WrappedComponent: React.ComponentType<P>): string {
  return WrappedComponent.displayName || WrappedComponent.name || 'Component';
}

interface User {
  id: string;
  name: string;
  roles: string[];
}

const authService = {
  currentUser: null as User | null,
  login: (user: User) => { authService.currentUser = user; },
  logout: () => { authService.currentUser = null; },
  getCurrentUser: () => authService.currentUser,
  hasRole: (role: string) => authService.currentUser?.roles.includes(role) || false,
  hasAnyRole: (roles: string[]) => roles.some(role => authService.hasRole(role)),
};

const adminUser: User = { id: 'u1', name: 'Admin User', roles: ['admin', 'viewer'] };
const editorUser: User = { id: 'u2', name: 'Editor User', roles: ['editor', 'viewer'] };
const viewerUser: User = { id: 'u3', name: 'Viewer User', roles: ['viewer'] };
const noRoleUser: User = { id: 'u4', name: 'Guest User', roles: [] };

function withAccessControl<P extends object>(
  WrappedComponent: React.ComponentType<P>,
  requiredRoles: string[],
  FallbackComponent?: React.ComponentType<any> 
): React.ComponentType<P> {
  class WithAccessControl extends React.Component<P> {
    static displayName = `withAccessControl(${getDisplayName(WrappedComponent)})`;

    render() {
      const hasAccess = authService.hasAnyRole(requiredRoles);

      if (!hasAccess) {
        if (FallbackComponent) {
          return <FallbackComponent {...this.props} />; // Pass props to fallback too
        } else {
          return <div style={{ color: 'red', fontWeight: 'bold' }}>Access Denied! Required roles: {requiredRoles.join(', ')}</div>;
        }
      }

      return <WrappedComponent {...this.props} />;
    }
  }

  return WithAccessControl;
}

const AdminDashboard: React.FC = () => <div>Welcome to the Admin Dashboard!</div>;
const EditorPanel: React.FC = () => <div>Edit content here.</div>;
const ViewerContent: React.FC = () => <div>View general content.</div>;

const AuthenticatedAdminDashboard = withAccessControl(AdminDashboard, ['admin']);
const AuthenticatedEditorPanel = withAccessControl(EditorPanel, ['editor']);
const AuthenticatedViewerContent = withAccessControl(ViewerContent, ['viewer']);

const FallbackMessage: React.FC = () => <div style={{ color: 'orange' }}>Please log in with sufficient privileges.</div>;
const SpecificFallbackAdminDashboard = withAccessControl(AdminDashboard, ['admin'], FallbackMessage);

const App: React.FC = () => {
  const [user, setUser] = React.useState<User | null>(authService.getCurrentUser());

  const handleLogin = (userToLogin: User) => {
    authService.login(userToLogin);
    setUser(authService.getCurrentUser());
  };

  const handleLogout = () => {
    authService.logout();
    setUser(authService.getCurrentUser());
  };

  return (
    <div>
      <h1>Access Control Demo (Current User: {user?.name || 'None'})</h1>
      <button onClick={() => handleLogin(adminUser)}>Login as Admin</button>
      <button onClick={() => handleLogin(editorUser)}>Login as Editor</button>
      <button onClick={() => handleLogin(viewerUser)}>Login as Viewer</button>
      <button onClick={() => handleLogin(noRoleUser)}>Login as Guest</button>
      <button onClick={handleLogout}>Logout</button>

      <hr/>
      <h2>Admin Section:</h2>
      <AuthenticatedAdminDashboard />
      <hr/>
      <h2>Editor Section:</h2>
      <AuthenticatedEditorPanel />
      <hr/>
      <h2>Viewer Section:</h2>
      <AuthenticatedViewerContent />
      <hr/>
      <h2>Admin Section with Custom Fallback:</h2>
      <SpecificFallbackAdminDashboard />
    </div>
  );
};

// ReactDOM.render(<App />, document.getElementById('root'));

