import React from 'react';

function getDisplayName<P extends object>(WrappedComponent: React.ComponentType<P>): string {
  return WrappedComponent.displayName || WrappedComponent.name || 'Component';
}

interface WithAuthProps { isAuthenticated: boolean; }
const SignInMessage: React.FC = () => <div style={{ padding: '10px', background: '#ffe0b2', border: '1px solid orange' }}>Please sign in to view this content.</div>;
const withAuth = <P extends object>(WrappedComponent: React.ComponentType<P & WithAuthProps>): React.ComponentType<P> => {
  return class WithAuth extends React.Component<P> {
    static displayName = `withAuth(${getDisplayName(WrappedComponent)})`;
    render() {
      const isAuthenticated = true; // Simulate authentication: true for main test
      if (!isAuthenticated) {
        return <SignInMessage />;
      }
      return <WrappedComponent {...this.props as P} isAuthenticated={isAuthenticated} />;
    }
  };
};

interface Theme { primaryColor: string; secondaryColor: string; }
interface WithThemeProps { theme: Theme; }
const withTheme = <P extends object>(WrappedComponent: React.ComponentType<P & WithThemeProps>): React.ComponentType<P> => {
  return class WithTheme extends React.Component<P> {
    static displayName = `withTheme(${getDisplayName(WrappedComponent)})`;
    render() {
      const theme = { primaryColor: '#2196F3', secondaryColor: '#FFC107' }; // Default theme
      return <WrappedComponent {...this.props as P} theme={theme} />;
    }
  };
};

const withLogger = <P extends object>(WrappedComponent: React.ComponentType<P>): React.ComponentType<P> => {
  return class WithLogger extends React.Component<P> {
    static displayName = `withLogger(${getDisplayName(WrappedComponent)})`;
    componentDidMount() { console.log(`${getDisplayName(WrappedComponent)} mounted.`); }
    componentWillUnmount() { console.log(`${getDisplayName(WrappedComponent)} unmounted.`); }
    render() { return <WrappedComponent {...this.props} />; }
  };
};

const compose = (...fns: Function[]) => (x: any) => fns.reduceRight((v, f) => f(v), x);

interface DashboardProps {
  isAuthenticated: boolean;
  theme: Theme;
  welcomeMessage?: string; // An example of an unrelated prop
}

const Dashboard: React.FC<DashboardProps> = ({ isAuthenticated, theme, welcomeMessage }) => {
  return (
    <div style={{ padding: '20px', border: `2px solid ${theme.primaryColor}`, color: theme.primaryColor }}>
      <h2 style={{ color: theme.secondaryColor }}>{welcomeMessage || 'Welcome to your Dashboard!'}</h2>
      <p>Authentication Status: {isAuthenticated ? 'Authenticated' : 'Not Authenticated'}</p>
      <p>Primary Color: {theme.primaryColor}</p>
      <p>Secondary Color: {theme.secondaryColor}</p>
      <p>This component is enhanced by multiple HOCs!</p>
    </div>
  );
};

// Compose HOCs in the specified order (right-to-left for compose)
// Order: Dashboard -> withAuth -> withTheme -> withLogger
const EnhancedDashboard = compose(
  withLogger,
  withTheme,
  withAuth
)(Dashboard);

const App: React.FC = () => {
  return (
    <div>
      <h1>Composed Dashboard:</h1>
      <EnhancedDashboard welcomeMessage="Hello User!" />
      <br/>
      <p>Check console for mount/unmount logs. Inspect component in DevTools for display name.</p>
    </div>
  );
};

