class ScrollableList extends React.Component {
  constructor(props) {
    super(props);
    this.listRef = React.createRef();
    this.state = { messages: [] };
  }

  componentDidMount() {
    // Simulate initial message load
    this.setState({ messages: Array.from({ length: 20 }, (_, i) => `Message ${i + 1}`) });
  }

  // Called right before the DOM is updated
  getSnapshotBeforeUpdate(prevProps, prevState) {
    // If we're adding new messages, capture scroll position
    if (prevState.messages.length < this.state.messages.length) {
      const list = this.listRef.current;
      // Return scrollHeight - scrollTop to maintain bottom position
      return list.scrollHeight - list.scrollTop;
    }
    return null;
  }

  // Called after the DOM is updated
  componentDidUpdate(prevProps, prevState, snapshot) {
    // If a snapshot was returned (meaning new messages were added)
    // and the user was at the bottom, maintain scroll position at the bottom
    if (snapshot !== null) {
      const list = this.listRef.current;
      list.scrollTop = list.scrollHeight - snapshot;
    }
  }

  addMessage = () => {
    this.setState(prevState => ({
      messages: [...prevState.messages, `New Message ${prevState.messages.length + 1}`]
    }));
  };

  render() {
    return (
      <div>
        <div
          ref={this.listRef}
          style={{ height: '200px', overflowY: 'scroll', border: '1px solid gray' }}
        >
          {this.state.messages.map((msg, index) => (
            <p key={index}>{msg}</p>
          ))}
        </div>
        <button onClick={this.addMessage}>Add New Message</button>
      </div>
    );
  }
}

class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false, error: null, errorInfo: null };
  }

  // This static method is called when an error is thrown in a child component.
  // It should return an object to update the state.
  static getDerivedStateFromError(error) {
    // Update state so the next render will show the fallback UI.
    return { hasError: true };
  }

  // This method is called after an error has been caught.
  // It's used for side effects like logging the error.
  componentDidCatch(error, errorInfo) {
    console.error("Error caught by Error Boundary:", error, errorInfo);
    this.setState({
      error: error,
      errorInfo: errorInfo
    });
  }

  render() {
    if (this.state.hasError) {
      // You can render any custom fallback UI
      return (
        <div>
          <h1>Something went wrong.</h1>
          {this.props.showDetails && this.state.error && (
            <details style={{ whiteSpace: 'pre-wrap' }}>
              {this.state.error && this.state.error.toString()}
              <br />
              {this.state.errorInfo.componentStack}
            </details>
          )}
        </div>
      );
    }

    return this.props.children;
  }
}

