import React from 'react';

class TypingIndicator extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showIndicator: false
    };
    this.typingTimeout = null;
  }

  componentDidUpdate(prevProps) {
    if (this.props.isTyping && !prevProps.isTyping) {
      // isTyping changed from false to true
      this.setState({ showIndicator: true });
      // Clear any existing timeout to avoid multiple indicators
      if (this.typingTimeout) {
        clearTimeout(this.typingTimeout);
      }
      // Set a new timeout to hide the indicator after 3 seconds
      this.typingTimeout = setTimeout(() => {
        this.setState({ showIndicator: false });
        this.typingTimeout = null;
      }, 3000);
    } else if (!this.props.isTyping && prevProps.isTyping) {
      // isTyping changed from true to false
      // Immediately hide indicator and clear any pending timeout
      if (this.typingTimeout) {
        clearTimeout(this.typingTimeout);
        this.typingTimeout = null;
      }
      this.setState({ showIndicator: false });
    }
  }

  componentWillUnmount() {
    // Clear timeout if component unmounts while indicator is visible
    if (this.typingTimeout) {
      clearTimeout(this.typingTimeout);
      this.typingTimeout = null;
    }
  }

  render() {
    if (this.state.showIndicator) {
      return <p>User is typing...</p>;
    }
    return null;
  }
}

