import React from 'react';

class UserCard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      user: null,
      loading: true,
      error: null
    };
    this.abortController = null; // For cancelling fetch requests
  }

  componentDidMount() {
    this.fetchUserData(this.props.userId);
  }

  componentDidUpdate(prevProps) {
    if (this.props.userId !== prevProps.userId) {
      // Cancel previous request if any
      if (this.abortController) {
        this.abortController.abort();
      }
      this.setState({ loading: true, error: null, user: null }); // Reset state for new fetch
      this.fetchUserData(this.props.userId);
    }
  }

  componentWillUnmount() {
    // Cancel any pending request if the component unmounts
    if (this.abortController) {
      this.abortController.abort();
    }
  }

  fetchUserData = async (userId) => {
    this.abortController = new AbortController();
    const signal = this.abortController.signal;
    try {
      const response = await fetch(`https://jsonplaceholder.typicode.com/users/${userId}`, { signal });
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      this.setState({ user: data, loading: false });
    } catch (error) {
      if (error.name === 'AbortError') {
        console.log('Fetch aborted for user:', userId);
        // Do not update state if the fetch was intentionally aborted
      } else {
        this.setState({ error: error, loading: false });
      }
    }
  };

  render() {
    const { user, loading, error } = this.state;

    if (loading) {
      return <div>Loading user data...</div>;
    }

    if (error) {
      return <div style={{ color: 'red' }}>Error: {error.message}</div>;
    }

    return (
      <div style={{ border: '1px solid #ccc', padding: '15px', borderRadius: '8px' }}>
        <h3>User Details</h3>
        <p><strong>Name:</strong> {user.name}</p>
        <p><strong>Email:</strong> {user.email}</p>
      </div>
    );
  }
}

