import React from 'react';

class ChatWindow extends React.Component {
  constructor(props) {
    super(props);
    this.state = { messages: [] };
    this.chatRef = React.createRef(); // Reference to the scrollable div
  }

  componentDidMount() {
    // Simulate initial messages
    this.setState({
      messages: Array.from({ length: 15 }, (_, i) => `Initial Message ${i + 1}`)
    });
  }

  getSnapshotBeforeUpdate(prevProps, prevState) {
    const chatElement = this.chatRef.current;
    if (chatElement) {
      // Check if we were scrolled to the bottom before the update
      const isScrolledToBottom = 
        chatElement.scrollTop + chatElement.clientHeight >= chatElement.scrollHeight;

      // If new messages are being added, capture scroll info
      if (this.state.messages.length > prevState.messages.length) {
        return {
          scrollHeight: chatElement.scrollHeight,
          scrollTop: chatElement.scrollTop,
          isScrolledToBottom: isScrolledToBottom
        };
      }
    }
    return null; // No snapshot needed
  }

  componentDidUpdate(prevProps, prevState, snapshot) {
    if (snapshot !== null) {
      const chatElement = this.chatRef.current;
      if (chatElement) {
        // Calculate the difference in scroll height (due to new messages)
        const scrollHeightDelta = chatElement.scrollHeight - snapshot.scrollHeight;

        if (snapshot.isScrolledToBottom) {
          // If user was at the bottom, scroll them to the new bottom
          chatElement.scrollTop = chatElement.scrollHeight;
        } else {
          // If user was scrolled up, maintain their relative position
          chatElement.scrollTop = snapshot.scrollTop + scrollHeightDelta;
        }
      }
    }
  }

  addMessage = () => {
    this.setState(prevState => ({
      messages: [...prevState.messages, `New Message ${prevState.messages.length + 1}`]
    }));
  };

  render() {
    return (
      <div style={{ padding: '20px' }}>
        <div
          ref={this.chatRef}
          style={{
            height: '300px',
            overflowY: 'scroll',
            border: '1px solid #ddd',
            padding: '10px',
            marginBottom: '10px'
          }}
        >
          {this.state.messages.map((msg, index) => (
            <p key={index} style={{ margin: '5px 0' }}>
              {msg}
            </p>
          ))}
        </div>
        <button onClick={this.addMessage}>Add New Message</button>
      </div>
    );
  }
}

