class LifecycleDemo extends React.Component {
  constructor(props) {
    super(props);
    this.state = { count: 0 };
    console.log('1. Constructor: Initializing state and binding methods.');
  }

  static getDerivedStateFromProps(nextProps, prevState) {
    console.log('2. getDerivedStateFromProps: Called before render, returns state update.');
    // Example: Update state based on props
    if (nextProps.initialCount && nextProps.initialCount !== prevState.count) {
      return { count: nextProps.initialCount };
    }
    return null; // No state update needed
  }

  componentDidMount() {
    console.log('4. componentDidMount: Component mounted to DOM. Good for data fetching.');
    this.timer = setInterval(() => {
      this.setState(prevState => ({ count: prevState.count + 1 }));
    }, 1000);
  }

  shouldComponentUpdate(nextProps, nextState) {
    console.log(`5. shouldComponentUpdate: Current count ${this.state.count}, Next count ${nextState.count}.`);
    // Only update if count changes
    return nextState.count !== this.state.count;
  }

  getSnapshotBeforeUpdate(prevProps, prevState) {
    console.log('6. getSnapshotBeforeUpdate: Called right before DOM updates.');
    // Example: Capture scroll position before update
    return 'captured snapshot';
  }

  componentDidUpdate(prevProps, prevState, snapshot) {
    console.log(`8. componentDidUpdate: Component updated. Snapshot: ${snapshot}`);
    console.log(`   Previous state: ${prevState.count}, Current state: ${this.state.count}`);
  }

  componentWillUnmount() {
    console.log('9. componentWillUnmount: Cleanup before component unmounts.');
    clearInterval(this.timer);
  }

  render() {
    console.log('3 & 7. Render: Building the Virtual DOM.');
    return (
      <div>
        <h2>Lifecycle Demo</h2>
