import React from 'react';

class Counter extends React.Component {
  constructor(props) {
    super(props);
    this.state = { count: 0 };
  }

  // Demonstrates asynchronous nature
  handleBadIncrement = () => {
    this.setState({ count: this.state.count + 1 });
    console.log('After setState (may not be updated):', this.state.count); // Logs old value
  }

  // Demonstrates functional setState for reliability
  handleGoodIncrement = () => {
    this.setState(prevState => {
      console.log('Inside functional setState (prevState):', prevState.count);
      return { count: prevState.count + 1 };
    });
    console.log('After functional setState (still may be old value outside):', this.state.count); // Logs old value
  }

  // Demonstrates setState with callback
  handleIncrementWithCallback = () => {
    this.setState(
      prevState => ({ count: prevState.count + 1 }),
      () => {
        console.log('Callback fired (state is updated):', this.state.count);
      }
    );
  }

  render() {
    return (
      <div>
        <h3>Counter: {this.state.count}</h3>
        <button onClick={this.handleBadIncrement}>Bad Increment (Async)</button>
        <button onClick={this.handleGoodIncrement}>Good Increment (Functional)</button>
        <button onClick={this.handleIncrementWithCallback}>Increment with Callback</button>
      </div>
    );
  }
}

export default Counter;

import React from 'react';

class DataDisplay extends React.Component {
  constructor(props) {
    super(props);
    this.state = { dataVersion: 0, content: 'Initial Content' };
  }

  updateContent = (newContent) => {
    this.setState(prevState => {
      // Only update if newContent is actually different
      if (prevState.content === newContent) {
        console.log('Content is the same, no update needed.');
        return null; // Prevents state update and re-render
      }
      console.log('Content changed, updating state.');
      return { content: newContent, dataVersion: prevState.dataVersion + 1 };
    });
  }

  render() {
    console.log('Rendered DataDisplay. Version:', this.state.dataVersion);
    return (
      <div>
        <p>{this.state.content}</p>
        <button onClick={() => this.updateContent('Updated Content')}>Update Content</button>
        <button onClick={() => this.updateContent('Initial Content')}>Set Initial Content</button>
      </div>
    );
  }
}

export default DataDisplay;

