import React from 'react';

class TimerComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      seconds: 0,
      isRunning: false
    };
    this.timerId = null;
    console.log('Constructor Called');
  }

  componentDidMount() {
    console.log('componentDidMount Called: Starting timer.');
    this.timerId = setInterval(() => {
      this.setState(prevState => {
        const newSeconds = prevState.seconds + 1;
        const isRunning = !this.props.limit || newSeconds <= this.props.limit;
        if (!isRunning && this.timerId) {
          clearInterval(this.timerId);
          this.timerId = null;
          console.log('Timer stopped due to limit.');
        }
        return { seconds: newSeconds, isRunning: isRunning };
      });
    }, 1000);
    this.setState({ isRunning: true });
  }

  componentDidUpdate(prevProps, prevState) {
    console.log('componentDidUpdate Called');
    // Handle limit prop changes or stopping the timer if limit is reached
    if (this.props.limit !== prevProps.limit && this.state.seconds < this.props.limit) {
        console.log(`Limit changed from ${prevProps.limit} to ${this.props.limit}.`);
        // If timer was stopped and limit increased, restart if needed
        if (!this.state.isRunning && this.timerId === null) {
            this.componentDidMount(); // Re-start timer if it was stopped
        }
    }
    // If the component reaches the limit AFTER componentDidMount check
    if (this.props.limit && this.state.seconds > this.props.limit && this.timerId) {
        clearInterval(this.timerId);
        this.timerId = null;
        this.setState({ isRunning: false });
        console.log('Timer stopped by componentDidUpdate due to limit.');
    }
  }

  componentWillUnmount() {
    console.log('componentWillUnmount Called: Clearing timer.');
    if (this.timerId) {
      clearInterval(this.timerId);
      this.timerId = null;
    }
  }

  shouldComponentUpdate(nextProps, nextState) {
    // Bonus: Only re-render if seconds or limit prop actually changes
    const shouldUpdate = (
      nextState.seconds !== this.state.seconds ||
      nextProps.limit !== this.props.limit
    );
    console.log(`shouldComponentUpdate: ${shouldUpdate ? 'Re-rendering' : 'Skipping render'}`);
    return shouldUpdate;
  }

  render() {
    console.log('Render Called');
    return (
      <div>
        <h3>Timer: {this.state.seconds}s</h3>
        {this.props.limit !== undefined && <p>Limit: {this.props.limit}s</p>}
      </div>
    );
  }
}

export default TimerComponent;

