    this.setState(prevState => ({
      count: prevState.count - 1
    }));
  };

  handleAsyncUpdate = () => {
    // This demonstrates the asynchronous nature of setState
    this.setState({ count: this.state.count + 10 });
    console.log('Inside handleClick (state might not be updated yet):', this.state.count);
    // The actual update will be reflected in the next render cycle.
