      if (prevState.count % 2 === 0) {
        console.log('Count is even, incrementing.');
        return { count: prevState.count + 1 };
      } else {
        console.log('Count is odd, no update needed.');
        return null; // Return null to prevent update
      }
    });
  };

  render() {
    console.log('Render Called. Current Count:', this.state.count);
    return (
      <div>
