        <button onClick={this.handleDecrement}>Decrement</button>
        <button onClick={this.handleAsyncUpdate}>Async +10 & Log</button>
        <button onClick={this.handleConditionalIncrement}>Increment if Even</button>
      </div>
    );
  }
}

