import React from 'react';

class LifecycleComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = { data: null, message: 'Loading...' };
    console.log('1. Constructor: Initializing state and binding methods.');
    this.handleClick = this.handleClick.bind(this);
  }

  static getDerivedStateFromProps(props, state) {
    console.log('2. getDerivedStateFromProps: Checking for prop changes to update state.');
    // Example: If a prop 'reset' changes, reset internal message.
    if (props.resetMessage && state.message !== 'Message Reset!') {
        return { message: 'Message Reset!' };
    }
    return null; // No state update needed from props
  }

  componentDidMount() {
    console.log('4. componentDidMount: Component mounted to DOM. Performing side effects.');
    // Best place for API calls, subscriptions, DOM manipulations
    fetch('https://jsonplaceholder.typicode.com/todos/1') // Example API call
      .then(response => response.json())
      .then(json => {
        console.log('API data fetched:', json);
        this.setState({ data: json.title, message: 'Data loaded!' });
      })
      .catch(error => console.error('Error fetching data:', error));

    // Example of adding an event listener
    window.addEventListener('resize', this.handleResize);
  }

  componentWillUnmount() {
    console.log('ComponentWillUnmount: Cleaning up event listener.');
    window.removeEventListener('resize', this.handleResize);
  }

  handleClick() {
    this.setState(prevState => ({ message: prevState.message + ' Clicked!' }));
  }

  handleResize = () => {
    console.log('Window resized!');
