class UpdatingComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = { count: 0, showMore: false };
    console.log('Constructor: Initializing state.');
  }

  static getDerivedStateFromProps(props, state) {
    console.log('getDerivedStateFromProps (Update): Deriving state from props.');
    // Example: Reset count if a specific prop changes
    if (props.resetCount && state.count !== 0) {
      return { count: 0 };
    }
    return null;
  }

  shouldComponentUpdate(nextProps, nextState) {
    console.log('shouldComponentUpdate: Deciding whether to re-render.');
    // Optimize: Only re-render if count or showMore state changes, or prop 'value' changes.
    return (
      nextProps.value !== this.props.value ||
      nextState.count !== this.state.count ||
      nextState.showMore !== this.state.showMore
    );
  }

  getSnapshotBeforeUpdate(prevProps, prevState) {
    console.log('getSnapshotBeforeUpdate: Capturing DOM info before update.');
    // Example: Capture scroll position before list updates
    if (this.props.list.length < prevProps.list.length) {
        const list = document.getElementById('my-list');
        if (list) {
            return list.scrollHeight - list.scrollTop; // Amount of scrollable content below the visible part
        }
    }
    return null;
  }

  componentDidUpdate(prevProps, prevState, snapshot) {
    console.log('componentDidUpdate: Component updated in DOM. Performing side effects.');
    // Example: Fetch new data if 'value' prop changes
    if (this.props.value !== prevProps.value) {
      console.log(`Value prop changed from ${prevProps.value} to ${this.props.value}. Fetching new data...`);
      // fetch(`/api/data?value=${this.props.value}`).then(...);
    }

    // Example: Use snapshot to adjust scroll position
    if (snapshot !== null) {
        console.log('Snapshot captured:', snapshot);
        const list = document.getElementById('my-list');
        if (list) {
            list.scrollTop = list.scrollHeight - snapshot;
        }
    }

    // Example: Conditionally update state (CAUTION: avoid infinite loops)
    if (this.state.count < 5 && prevState.count === 0) {
        // this.setState({ count: 1 }); // Would cause an extra re-render on initial update
    }
  }

  handleClick = () => {
    this.setState(prevState => ({ count: prevState.count + 1 }));
  };

  toggleShowMore = () => {
    this.setState(prevState => ({ showMore: !prevState.showMore }));
  };

  render() {
    console.log('render (Update): Rendering component JSX with updated props/state.');
    return (
      <div>
        <h2>Updating Phase Demo</h2>
        <p>Prop Value: {this.props.value}</p>
        <p>State Count: {this.state.count}</p>
        <button onClick={this.handleClick}>Increment Count</button>
        <button onClick={this.toggleShowMore}>Toggle More Info</button>
        {this.state.showMore && <p>More info is visible now!</p>}
        <div id="my-list" style={{ height: '100px', overflowY: 'scroll', border: '1px solid black' }}>
          {this.props.list.map((item, index) => <p key={index}>{item}</p>)}
        </div>
      </div>
    );
  }
}

