import React from 'react';

class PostViewer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      postData: null,
      loading: true,
      error: null
    };
    console.log('PostViewer: Constructor called');
  }

  componentDidMount() {
    console.log('PostViewer: componentDidMount called. Fetching data...');
    fetch('https://jsonplaceholder.typicode.com/posts/1')
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        this.setState({ postData: data, loading: false });
        console.log('PostViewer: Data fetched successfully.');
      })
      .catch(error => {
        this.setState({ error: error, loading: false });
        console.error('PostViewer: Error fetching data:', error);
      });
  }

  componentWillUnmount() {
    console.log('PostViewer: componentWillUnmount called. Cleaning up any subscriptions/timers.');
    // For example, if you had a WebSocket connection or a timer, you'd close/clear it here.
  }

  render() {
    console.log('PostViewer: render called');
    const { postData, loading, error } = this.state;

    if (loading) {
      return <div>Loading post...</div>;
    }

    if (error) {
      return <div style={{ color: 'red' }}>Error: {error.message}</div>;
    }

    return (
      <div style={{ border: '1px solid #ccc', padding: '15px', borderRadius: '8px' }}>
        <h3>{postData.title}</h3>
        <p>{postData.body}</p>
      </div>
    );
  }
}

export default PostViewer;

