import React from 'react';

class ChatMessageList extends React.Component {
  constructor(props) {
    super(props);
    this.listRef = React.createRef();
    console.log('ChatMessageList: Constructor');
  }

  getSnapshotBeforeUpdate(prevProps, prevState) {
    console.log('ChatMessageList: getSnapshotBeforeUpdate');
    // Are we adding new messages?
    if (this.props.messages.length > prevProps.messages.length) {
      const list = this.listRef.current;
      if (list) {
        // Check if the user was scrolled to the bottom (or very near the bottom)
        const isScrolledToBottom = list.scrollTop + list.clientHeight >= list.scrollHeight - 20; // 20px buffer
        if (isScrolledToBottom) {
          return list.scrollHeight; // Return the current scrollHeight BEFORE the DOM update
        }
      }
    }
    return null; // If not adding messages or not at bottom, no snapshot needed
  }

  componentDidUpdate(prevProps, prevState, snapshot) {
    console.log('ChatMessageList: componentDidUpdate');
    // If a snapshot was returned, it means we were scrolled to the bottom before the update
    if (snapshot !== null) {
      const list = this.listRef.current;
      if (list) {
        // Adjust scroll position to keep the user at the new bottom
        // new scrollTop = current scrollHeight - (old scrollHeight from snapshot)
        list.scrollTop = list.scrollHeight - snapshot;
        console.log(`ChatMessageList: Adjusted scroll to new bottom. New scrollTop: ${list.scrollTop}`);
      }
    } else {
        console.log('ChatMessageList: No snapshot, preserving user scroll position.');
    }
  }

  render() {
    console.log('ChatMessageList: Render');
    const { messages } = this.props;
    return (
      <div
        ref={this.listRef}
        style={{
          height: '250px',
          border: '1px solid #ccc',
          overflowY: 'auto',
          padding: '10px',
          fontFamily: 'monospace',
          whiteSpace: 'pre-wrap',
          display: 'flex',
          flexDirection: 'column-reverse' // Optional: Renders messages from bottom up
        }}
      >
        {[...messages].reverse().map((msg, index) => (
          <div key={index} style={{ marginBottom: '5px' }}>{msg}</div>
        ))}
      </div>
    );
  }
}

