    // Check if animationActive state has changed
    if (nextState.animationActive !== this.state.animationActive) {
      console.log('SCU: Animation state changed');
      return true;
    }

    // Check if progress prop has changed significantly
    // Using Math.abs to handle small float differences and comparing with 1 unit threshold
    if (Math.abs(nextProps.progress - this.props.progress) > 1) {
      console.log('SCU: Progress changed significantly');
      return true;
