class CountdownTimer extends React.Component {
  state = {
    secondsLeft: this.props.initialSeconds,
  };
  timerId = null; // To store the interval ID for cleanup

  componentDidMount() {
    console.log('CountdownTimer: componentDidMount - Starting timer.');
    // Start the countdown timer
    this.timerId = setInterval(() => {
      this.setState(prevState => {
        if (prevState.secondsLeft <= 1) {
          clearInterval(this.timerId);
          console.log('CountdownTimer: Timer finished.');
          return { secondsLeft: 0 };
        }
        return { secondsLeft: prevState.secondsLeft - 1 };
      });
    }, 1000);
  }

  // Optional: If initialSeconds can change during component's lifetime
  componentDidUpdate(prevProps) {
    if (this.props.initialSeconds !== prevProps.initialSeconds) {
      console.log('CountdownTimer: componentDidUpdate - initialSeconds changed, resetting timer.');
      // Clear existing timer if prop changes, then restart
      if (this.timerId) {
        clearInterval(this.timerId);
      }
      this.setState({ secondsLeft: this.props.initialSeconds });
      this.componentDidMount(); // Re-call mount logic to restart timer
    }
  }

  componentWillUnmount() {
    console.log('CountdownTimer: componentWillUnmount - Clearing timer.');
    // Crucial: Clean up the timer to prevent memory leaks
    if (this.timerId) {
      clearInterval(this.timerId);
      this.timerId = null;
    }
  }

  render() {
    console.log('CountdownTimer: render');
    const { secondsLeft } = this.state;
    return (
      <div>
        <h2>Countdown: {secondsLeft} seconds</h2>
        {secondsLeft <= 0 && <p>Time's Up!</p>}
      </div>
    );
  }
}

