import React from 'react';

class LifecycleLogger extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      count: 0,
      displayMessage: props.message || 'No message'
    };
    console.log('Constructor (mount)');
  }

  static getDerivedStateFromProps(nextProps, prevState) {
    console.log('1. static getDerivedStateFromProps');
    // Update displayMessage if message prop changes
    if (nextProps.message !== prevState.displayMessage) {
      return {
        displayMessage: nextProps.message
      };
    }
    return null; // No state update needed from props
  }

  shouldComponentUpdate(nextProps, nextState) {
    console.log('2. shouldComponentUpdate');
    // Return true to allow rendering for this task
    return true;
  }

  render() {
    console.log('3. render');
    return (
      <div>
        <p>Count: {this.state.count}</p>
        <p>Message: {this.state.displayMessage}</p>
        <button onClick={() => this.setState(prevState => ({ count: prevState.count + 1 }))}>
          Increment Count
        </button>
      </div>
    );
  }

  getSnapshotBeforeUpdate(prevProps, prevState) {
    console.log('4. getSnapshotBeforeUpdate');
    // Return a simple snapshot string
    return 'Snapshot taken: ' + prevState.count; // Example: Capture old count
  }

  componentDidUpdate(prevProps, prevState, snapshot) {
    console.log('5. componentDidUpdate');
    console.log('Snapshot received:', snapshot);
    if (this.props.message !== prevProps.message) {
      console.log('Message prop changed from "' + prevProps.message + '" to "' + this.props.message + '"');
    }
  }

  componentWillUnmount() {
    console.log('componentWillUnmount (cleanup)');
  }
}

