import React from 'react';

class ControlledInputWithReset extends React.Component {
  state = {
    inputValue: this.props.externalValue || '',
  };

  static getDerivedStateFromProps(nextProps, prevState) {
    // Only update internal state if the externalValue prop has genuinely changed
    // and is different from the current internal state (to avoid unnecessary updates).
    if (nextProps.externalValue !== prevState.inputValue) {
      return {
        inputValue: nextProps.externalValue,
      };
    }
    return null; // No state update needed
  }

  handleChange = (event) => {
    this.setState({ inputValue: event.target.value });
  };

  render() {
    return (
      <div>
        <label>Input Value: </label>
        <input
          type="text"
          value={this.state.inputValue}
          onChange={this.handleChange}
        />
        <p>Prop received: {this.props.externalValue}</p>
      </div>
    );
  }
}

