import React from 'react';

class DataDisplay extends React.Component {
  // Simulate an expensive rendering operation
  simulateExpensiveRender() {
    let sum = 0;
    for (let i = 0; i < 10000000; i++) {
      sum += Math.sqrt(i);
    }
    console.log(`Expensive calculation sum: ${sum.toFixed(2)}`);
  }

  shouldComponentUpdate(nextProps, nextState) {
    // Check if version prop has changed
    if (nextProps.version !== this.props.version) {
      console.log('SCU: Returning true - version prop changed.');
      return true;
    }

    // Check if data array length has changed
    if (nextProps.data.length !== this.props.data.length) {
      console.log('SCU: Returning true - data array length changed.');
      return true;
    }

    // Check if any item's 'value' in data array has changed (shallow content comparison)
    // Assuming order of items is stable and items are objects with 'id' and 'value'
    for (let i = 0; i < nextProps.data.length; i++) {
      // Check if item exists at index and its value has changed
      if (!this.props.data[i] || nextProps.data[i].value !== this.props.data[i].value) {
        console.log(`SCU: Returning true - data item at index ${i} value changed.`);
        return true;
      }
    }

    console.log('SCU: Returning false - no significant prop/state changes.');
    return false; // No significant change, prevent re-render
  }

  render() {
    console.log('DataDisplay: render method called');
    this.simulateExpensiveRender(); // Call the expensive function

    return (
      <div>
        <h3>Data Version: {this.props.version}</h3>
        <ul>
          {this.props.data.map(item => (
            <li key={item.id}>{item.value}</li>
          ))}
        </ul>
      </div>
    );
  }
}

