import React from 'react';

class ChatWindow extends React.Component {
  chatContainerRef = React.createRef();

  // Optional: Scroll to bottom on initial mount or when messages are added to bottom
  componentDidMount() {
    this.scrollToBottom();
  }

  scrollToBottom = () => {
    const chatContainer = this.chatContainerRef.current;
    if (chatContainer) {
      chatContainer.scrollTop = chatContainer.scrollHeight;
    }
  };

  getSnapshotBeforeUpdate(prevProps, prevState) {
    const chatContainer = this.chatContainerRef.current;

    // Only capture snapshot if messages are being added and this is a scenario
    // where we need to adjust scroll (e.g., adding to top).
    // This condition checks if new messages were added and the component is scrollable.
    if (chatContainer && this.props.messages.length > prevProps.messages.length) {
      const scrollOffset = chatContainer.scrollHeight - chatContainer.scrollTop;
      console.log(`getSnapshotBeforeUpdate: Captured scroll offset: ${scrollOffset}px`);
      return scrollOffset;
    }
    return null; // No snapshot needed
  }

  componentDidUpdate(prevProps, prevState, snapshot) {
    const chatContainer = this.chatContainerRef.current;

    // Only apply adjustment if a snapshot was captured
    if (snapshot !== null && chatContainer) {
      // Adjust scroll position to maintain user's view relative to the bottom
      chatContainer.scrollTop = chatContainer.scrollHeight - snapshot;
      console.log(`componentDidUpdate: Adjusted scroll to ${chatContainer.scrollTop}px using snapshot: ${snapshot}px`);
    } else if (this.props.messages.length > prevProps.messages.length && snapshot === null) {
      // If new messages are added but no snapshot was taken (e.g., adding to bottom),
      // or it's an initial render, scroll to bottom.
      this.scrollToBottom();
    }
  }

  render() {
    return (
      <div
        ref={this.chatContainerRef}
        style={{
          height: '300px',
          overflowY: 'scroll',
          border: '1px solid #ccc',
          padding: '10px',
          display: 'flex',
          flexDirection: 'column-reverse' // To make new messages appear at top visually
        }}
      >
        {/* Reverse order for chat messages: newest at bottom (but UI displays them top-down) */}
        {/* To make messages appear at the bottom of the scroll container, and new messages push old ones up,*/}
        {/* the flex-direction: column-reverse is used here, so the rendering order in JSX needs to be adjusted. */}
        {/* If new messages are prepended to the array, they will appear visually at the top due to column-reverse. */}
        {this.props.messages.slice().reverse().map((msg, index) => (
          <div key={index} style={{ marginBottom: '5px', background: '#f0f0f0', padding: '5px' }}>
            {msg}
          </div>
        ))}
      </div>
    );
  }
}

