  componentDidUpdate(prevProps) {
    // Only re-fetch data if the productId prop has changed
    if (this.props.productId !== prevProps.productId) {
      this.fetchProductData(this.props.productId);
    }
  }

  fetchProductData = async (productId) => {
    this.setState({ loading: true, error: null, product: null }); // Reset state before new fetch
    try {
      // Using a public API for demonstration: JSONPlaceholder or FakeStoreAPI
      const response = await fetch(`https://fakestoreapi.com/products/${productId}`);
      if (!response.ok) {
        // For 404 or other non-200 responses, throw an error
        throw new Error(`HTTP error! Status: ${response.status} for product ID: ${productId}`);
      }
      const data = await response.json();
      this.setState({ product: data, loading: false });
    } catch (error) {
      console.error('Fetch error for product ID ' + productId + ':', error);
      this.setState({ error: error.message, loading: false });
    }
  };

  render() {
    const { product, loading, error } = this.state;
    const { productId } = this.props;

    if (loading) {
      return <div>Loading product {productId} details...</div>;
    }

    if (error) {
      return <div>Error loading product {productId}: {error}</div>;
    }

    // Handle case where product might be null even if no error (e.g., initial state)
    if (!product || Object.keys(product).length === 0) {
        return <div>No product data found for ID: {productId}.</div>;
    }

    return (
      <div style={{ border: '1px solid #eee', padding: '15px', borderRadius: '8px', maxWidth: '400px' }}>
        <h2>{product.title}</h2>
        <p style={{ color: '#555' }}>Category: {product.category}</p>
        <p style={{ fontWeight: 'bold', fontSize: '1.2em' }}>Price: ${product.price?.toFixed(2)}</p>
        <img src={product.image} alt={product.title} style={{ width: '100%', maxHeight: '200px', objectFit: 'contain', marginBottom: '10px' }} />
        <p>{product.description}</p>
      </div>
    );
  }
}

