  }
  render() {
    if (this.state.hasError) {
      return <div style={{ border: '2px solid purple', padding: '10px', margin: '10px' }}>
               <p>A rendering error occurred in a child. (Caught by GenericErrorBoundary)</p>
             </div>;
    }
    return this.props.children;
  }
}

const ActionButtons: React.FC = () => {
  const [status, setStatus] = useState<string>('Ready');

  const handleSyncError = () => {
    try {
      // Error in event handler - caught by local try/catch
      throw new Error('Simulated synchronous error in onClick!');
    } catch (error: any) {
      console.error('Locally caught sync error:', error.message);
      setStatus(`Sync Error Handled: ${error.message}`);
      // Optionally, show a temporary error message in the UI
    }
  };

  const handleAsyncError = () => {
    setStatus('Attempting async error...');
    // Error in async code (setTimeout callback) - needs try/catch inside callback
    setTimeout(() => {
      try {
        throw new Error('Simulated asynchronous error in setTimeout!');
      } catch (error: any) {
        console.error('Locally caught async error:', error.message);
        setStatus(`Async Error Handled: ${error.message}`);
      }
    }, 1000);

    // Example with Promise rejection
