      console.error('Locally caught promise error:', error.message);
      setStatus(`Promise Error Handled: ${error.message}`);
    });
    */
  };

  // Example of an error that WOULD be caught by GenericErrorBoundary
  const triggerRenderError = () => {
    // Simulate a rendering error for the ErrorBoundary to catch
    throw new Error('Simulated rendering error!');
  };

  return (
    <div style={{ border: '1px solid gray', padding: '20px', margin: '20px' }}>
