import React, { Component } from 'react';

interface Item {
  id: number;
  name: string;
  active: boolean;
}

interface FilteredListProps {
  items: Item[];
}

interface FilteredListState {
  filteredItems: Item[];
  prevItems: Item[] | null; // Used with getDerivedStateFromProps
}

// Approach 1: Using componentDidUpdate safely
class FilteredListA extends Component<FilteredListProps, FilteredListState> {
  constructor(props: FilteredListProps) {
    super(props);
    this.state = {
      filteredItems: this.props.items.filter(item => item.active),
      prevItems: null
    };
  }

  componentDidUpdate(prevProps: FilteredListProps) {
    // GOOD: Check if props actually changed to avoid infinite loop
    if (prevProps.items !== this.props.items) {
      console.log('FilteredListA: Items prop changed, updating state via componentDidUpdate');
      this.setState({
        filteredItems: this.props.items.filter(item => item.active)
      });
    }
  }

  render() {
    return (
      <div>
        <h3>Filtered List (componentDidUpdate)</h3>
        <ul>
          {this.state.filteredItems.map(item => (
            <li key={item.id}>{item.name} (Active: {String(item.active)})</li>
          ))}
        </ul>
      </div>
    );
  }
}

// Approach 2: Using static getDerivedStateFromProps (Recommended for prop-derived state)
class FilteredListB extends Component<FilteredListProps, FilteredListState> {
  constructor(props: FilteredListProps) {
    super(props);
    this.state = {
      filteredItems: [], // Initial state, will be updated by getDerivedStateFromProps
      prevItems: null,   // Store previous items to detect changes
    };
  }

  static getDerivedStateFromProps(props: FilteredListProps, state: FilteredListState) {
    // Check if the items prop has changed since the last render
    if (props.items !== state.prevItems) {
      console.log('FilteredListB: Items prop changed, deriving new state via getDerivedStateFromProps');
      return {
        filteredItems: props.items.filter(item => item.active),
        prevItems: props.items, // Update prevItems for the next comparison
      };
    }
    // No change in props, no state update needed
    return null;
  }

  render() {
    return (
      <div>
        <h3>Filtered List (getDerivedStateFromProps)</h3>
        <ul>
          {this.state.filteredItems.map(item => (
            <li key={item.id}>{item.name} (Active: {String(item.active)})</li>
          ))}
        </ul>
      </div>
    );
  }
}

