class OptimizableComponent extends Component<OptimizableComponentProps, OptimizableComponentState> {
  constructor(props: OptimizableComponentProps) {
    super(props);
    this.state = { counter: 0 };
  }

  shouldComponentUpdate(nextProps: OptimizableComponentProps, nextState: OptimizableComponentState): boolean {
    // Only re-render if 'id' prop or 'counter' state changes.
    // 'name' and 'value' prop changes will NOT trigger a re-render.
    if (nextProps.id !== this.props.id || nextState.counter !== this.state.counter) {
