  return (
    <div style={{ border: '1px solid green', margin: '5px', padding: '5px' }}>
      <h4>Memoized Component (Basic)</h4>
      <p>Message: {message}</p>
      <p>Count: {count}</p>
    </div>
  );
};

// Wrap the component with React.memo
const MemoizedMessageDisplay = React.memo(MessageDisplay);

