import React, { useState, useEffect } from 'react';

interface Post {
  id: number;
  title: string;
  body: string;
}

const PostsFetcher: React.FC = () => {
  const [posts, setPosts] = useState<Post[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<Error | null>(null);

  useEffect(() => {
    console.log('useEffect: Component Mounted, fetching posts...');
    const fetchPosts = async () => {
      try {
        const response = await fetch('https://jsonplaceholder.typicode.com/posts?_limit=5');
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data: Post[] = await response.json();
        setPosts(data);
      } catch (err) {
        setError(err as Error);
      } finally {
        setLoading(false);
      }
    };

    fetchPosts();

    // Optional: Cleanup function if there was a subscription or timer
    return () => {
      console.log('useEffect: Cleanup on unmount (e.g., abort fetch request)');
      // Example: If using AbortController for fetch cleanup:
      // controller.abort(); 
    };
  }, []); // Empty dependency array means run once on mount and cleanup on unmount

  if (loading) return <div>Loading posts...</div>;
  if (error) return <div>Error: {error.message}</div>;

  return (
    <div>
      <h2>Blog Posts (Fetched with useEffect)</h2>
      <ul>
        {posts.map(post => (
          <li key={post.id}>
            <strong>{post.title}</strong>
            <p>{post.body.substring(0, 50)}...</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

// Usage:
// <PostsFetcher />

import React, { useState, useEffect } from 'react';

const DebouncedInput: React.FC = () => {
  const [inputValue, setInputValue] = useState('');
  const [debouncedValue, setDebouncedValue] = useState('');

  useEffect(() => {
    console.log(`useEffect: Input value changed to "${inputValue}", setting up debounce...`);
    // Set up a debounce timer
    const timerId = setTimeout(() => {
      setDebouncedValue(inputValue);
      console.log(`Debounced value set to: "${inputValue}"`);
    }, 500);

    // Cleanup function: Clear the previous timer if inputValue changes before timer fires
    return () => {
      console.log(`useEffect: Cleaning up previous debounce timer for "${inputValue}"`);
      clearTimeout(timerId);
    };
  }, [inputValue]); // Dependency array: re-run effect when inputValue changes

  return (
    <div style={{ border: '1px solid purple', margin: '5px', padding: '5px' }}>
      <h4>Debounced Input (useEffect for Updates & Cleanup)</h4>
      <input
        type="text"
        value={inputValue}
        onChange={(e) => setInputValue(e.target.value)}
        placeholder="Type something..."
      />
      <p>Current Input: {inputValue}</p>
      <p>Debounced Value: {debouncedValue}</p>
    </div>
  );
};

// Usage:
// <DebouncedInput />

