// Original problematic code:
class MyComponent extends React.Component<{ items: any[] }, { filteredItems: any[] }> {
  state = { filteredItems: [] };

  componentDidUpdate(prevProps: { items: any[] }) {
    // PROBLEM: This causes an infinite loop!
    this.setState({ filteredItems: this.props.items.filter(item => item.active) });
  }
  render() { /* ... */ }
}

// Corrected version using componentDidUpdate:
class MyComponentCorrected extends React.Component<{ items: any[] }, { filteredItems: any[] }> {
  state = { filteredItems: [] };

  componentDidUpdate(prevProps: { items: any[] }) {
    if (prevProps.items !== this.props.items) {
      this.setState({ filteredItems: this.props.items.filter(item => item.active) });
    }
  }
  render() { /* ... */ }
}

// Preferred modern approach using getDerivedStateFromProps:
class MyComponentGDSP extends React.Component<{ items: any[] }, { filteredItems: any[]; prevItems: any[] | null }> {
  constructor(props: { items: any[] }) {
    super(props);
    this.state = { filteredItems: [], prevItems: null };
  }

  static getDerivedStateFromProps(props: { items: any[] }, state: { prevItems: any[] | null }) {
    if (props.items !== state.prevItems) {
      return {
        filteredItems: props.items.filter(item => item.active),
        prevItems: props.items
      };
    }
    return null;
  }
  render() { /* ... */ }
}
```

```typescript
import React, { Component } from 'react';

interface MyComponentProps {
  value: number;
}

interface MyComponentState {
  derivedValue: number;
  lastValue: number | null;
}

class MyComponent extends Component<MyComponentProps, MyComponentState> {
  constructor(props: MyComponentProps) {
    super(props);
    this.state = {
      derivedValue: props.value * 2,
      lastValue: props.value,
    };
  }

  static getDerivedStateFromProps(nextProps: MyComponentProps, prevState: MyComponentState) {
    // Only update derivedValue if the 'value' prop has actually changed
    if (nextProps.value !== prevState.lastValue) {
      return {
        derivedValue: nextProps.value * 2,
        lastValue: nextProps.value,
      };
    }
    // No change in 'value' prop, so no state update is needed
    return null;
  }

  render() {
    return (
      <div>
        <p>Original Value: {this.props.value}</p>
        <p>Derived Value (from state): {this.state.derivedValue}</p>
      </div>
    );
  }
}
```

