import React, { Component } from 'react';

interface Item {
  id: number;
  name: string;
  isActive: boolean;
}

interface FilterableItemListProps {
  items: Item[];
}

interface FilterableItemListState {
  filteredItems: Item[];
  prevItemsProp: Item[] | null; // Used to track previous items prop
}

class FilterableItemList extends Component<FilterableItemListProps, FilterableItemListState> {
  constructor(props: FilterableItemListProps) {
    super(props);
    this.state = {
      filteredItems: [],
      prevItemsProp: null,
    };
  }

  static getDerivedStateFromProps(nextProps: FilterableItemListProps, prevState: FilterableItemListState) {
    // Check if the items prop has changed (by reference)
    if (nextProps.items !== prevState.prevItemsProp) {
      console.log('getDerivedStateFromProps: Items prop changed, recalculating filteredItems');
      return {
        filteredItems: nextProps.items.filter(item => item.isActive),
        prevItemsProp: nextProps.items, // Update prevItemsProp for the next comparison
      };
    }
    // If items prop has not changed, return null to indicate no state update
    return null;
  }

  render() {
    return (
      <div style={{ border: '1px solid gray', padding: '10px', margin: '10px' }}>
        <h3>Filtered Items (Active)</h3>
        {this.state.filteredItems.length === 0 ? (
          <p>No active items.</p>
        ) : (
          <ul>
            {this.state.filteredItems.map(item => (
              <li key={item.id}>{item.name}</li>
            ))}
          </ul>
        )}
      </div>
    );
  }
}

export default FilterableItemList;

